import React, { useState, useEffect } from 'react';
import './home.css';

const name = "Ossian Nörthen";
const desc = "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat."

const Title = () => {
  const [titleClass, setTitleClass] = useState('title');

  const handleImgTransition = () => {
    setTimeout(() => {
      setTitleClass('setTitleClass');
    }, 2000);
  };

  useEffect(() => {
    handleImgTransition();
  })

  return (
    <div className={titleClass}>
      <h1 className="titleHeading">{ name }</h1>
      <p className="titleDescription">{ desc }</p>
    </div>
  );
};

  export default Title;
