import React from 'react';
import './splash.css';

const name = "Ossian Nörthen";
const desc = "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat."

class Title extends React.Component {
  state = {
    opacity: 0,
    buttonClassName: 'about-me-button',
    innerHTMLP: 'About me',
  }

  componentDidMount() {
    this.handleTimer()
  }

  handleTimer = () => {
    setTimeout(() => {
      this.setState({
        opacity: 1
      })
    }, 1600)
  }

  handleDescButtonClick = (e) => {
    const target = e.target;
    if (target.className === 'about-me-button') {
      this.setState({
        buttonClassName: 'about-me-button-open',
        innerHTMLP: desc,
      })
    } else {
      this.setState({
        buttonClassName: 'about-me-button',
        innerHTMLP: 'About me'
      })
    }
  }

    render () {
      return (
        <div className="title">
            <h1 className="title-heading" > { name } </h1>
            <button style={{opacity: this.state.opacity}} className={this.state.buttonClassName} onClick={this.handleDescButtonClick}>
              <p>{this.state.innerHTMLP}</p>
            </button>
        </div>
      )
    }
  };

  export default Title;
